import React from 'react';
import Rating from '../components/rating/rating';
import data from '../data/data';
import classes from './ProductScreen.module.css';
import styles from 'bootstrap/dist/bootstrap.min.css';


const productscreen =(props)=>{
    
    const product = data.product.find((x)=>x.id === props.match.params.id);
    //find is used to find the product by product id in the url
    //params is used to fetch the id part in url
    if(!product){
        <div class={styles.text-center}>Product Not Found</div>
    }

    return(
        <div className={classes.row_flex}>
            <div class={styles.text-center} >
                <img className={classes.product_image} src={product.image}/>
            </div>

            <div class="col-sm-3">

                <h5> {product.name}</h5>
                <div>
                    <Rating rating={product.rating}/>
                    <span>{product.numReviews} Reviews</span>
                </div>
                <p>Description : {product.description}</p>
               
            </div>

            <div class="col-sm-4"  >
                <div className={classes.product_info}>
                    <div className={classes.row_flex}>
                        <p>Price</p>
                        <p>{product.price}</p>

                    </div>
                    <div className={classes.row_flex}>
                        <p>Status</p>
                        <p>In Stock</p>

                    </div>
                    <div class="text-center">
                        <button class="btn btn-success ">Add to Cart</button>
                    </div>
                    
                </div>
                
            </div>
            

          
           
        </div>
             
    )
}

export default productscreen;
