import { PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "./Actionconstants";
import Axios from 'axios';
import { useDispatch } from "react-redux";

const ListProducts=async (dispatch)=>{
   
    dispatch({
        type:PRODUCT_LIST_REQUEST
    });
    try{
        const {data}=Axios.get('http://localhost:5000/api/products');
        return{
            type:PRODUCT_LIST_SUCCESS,payload:data
        }
    }
    catch(err){
        const error=err.message;
        return{
            type:PRODUCT_LIST_FAIL,payload:error
        }
    
    }
}
export default ListProducts;
