import {createStore,combineReducers,applyMiddleware,compose} from 'redux';
import {ProductListReducer} from './HomeReducer';
import thunk from 'redux-thunk'; //redux - thunk
import { ProductScreenReducer } from './ProductReducer';
import { CartReducer } from './CartReducer';
import {Signinreducer} from './Signinreducer';
import { RegisterReducer } from './RegisterReducer';


const iniitialState={
  signin:{
    user:localStorage.getItem('shippingaddress')
    ? JSON.parse(localStorage.getItem('shippingaddress')):
    {}
  },
addtocart:{
  shippingaddress:localStorage.getItem('shippingaddress')
  ? JSON.parse(localStorage.getItem('shippingaddress')):
  {},
}
}

function saveToLocalStorage(state1,state2) {
    try {
      const serialisedState = JSON.stringify(state1);
      localStorage.setItem("persistantState", serialisedState);

      const userinfo = JSON.stringify(state2);
      localStorage.setItem("UserState", userinfo);

    } catch (e) {
      console.warn(e);
    }
  }
  
  // load string from localStarage and convert into an Object
  // invalid output must be undefined
  function loadFromLocalStorage() {
    try {
      const serialisedState = localStorage.getItem("persistantState");

      const userinfo = localStorage.getItem("UserState");
      if (serialisedState&&userinfo === null) return undefined;
      return JSON.parse(serialisedState,userinfo);
    } catch (e) {
      console.warn(e);
      return undefined;
    }
  }

