import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import classes from "./Signin.module.css";

const Signin=()=>{

    const [email,setemail]=useState('');

    const[password,setpassword]=useState('');

    dispatch=useDispatch();
    
    const onSubmitHandler=()=>{
        console.log(password,email)

        dispatch(Signin(email));
        
    }

    return(
        <div>
            <h2 className={classes.signin}>Sign In</h2>

            <div  class="row">
                    <div class="col-sm-4">
                        
                    </div>
        
                    <div class={`col-sm-4 ${classes.maindiv}`}>
                        <form onClick={()=>onSubmitHandler()}>
                            <div>
                                <label>Email</label>
                                <input class="form-control" onChange={(e)=>setemail(e.target.value)} placeholder="Enter Email" type="email"/>
        
                            </div>
        
                            <div className={classes.subdiv}>
                                <label>Password</label>
                                <input class="form-control" onChange={(e)=>setemail(e.target.value)} placeholder="Enter Password" type="password"/>
                            </div>
        
                            <button class={classes.btn}>
                                Sign In
                            </button>
        
                            <div>
                                <span>New Customer?</span>
                                <a href="/"><span>Create your account</span></a>
                            </div>
                            
                            
        
                        </form>

                        <p></p>
        
                    </div>
        
                    <div class="col-sm-4">
                        
                    </div>
            </div>

           

        </div>
    )
}

export default Signin;
