import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import classes from "./Signin.module.css";
import {Signinaction} from '../../Store/Action';


const Signin=(props)=>{

    const [email,setemail]=useState('');

    const[password,setpassword]=useState('');

    const dispatch=useDispatch();

    const signininfo=useSelector((state)=>state.signin);

    const {user,message}=signininfo;

    const redirect= props.location.search?props.location.search.split('=')[1]:'/';
    
    const onSubmitHandler=(email,password)=>{

        dispatch(Signinaction(email,password));
        
    }

    useEffect(()=>{
        if(user!==[]){
            props.history.push(redirect);
        }
        if(user==[]){

        }
        
    },[,redirect])
    return(
        <div>
            <h2 className={classes.signin}>Sign In</h2>

            <div  class="row">
                    <div class="col-sm-4">
                        
                    </div>
        
                    <div class={`col-sm-4 ${classes.maindiv}`}>
                        {/*<form>*/}
                        
                            <div>
                                <label>Email</label>
                                <input class="form-control" onChange={(e)=>setemail(e.target.value)} placeholder="Enter Email" type="email"/>
        
                            </div>
        
                            <div className={classes.subdiv}>
                                <label>Password</label>
                                <input class="form-control" onChange={(e)=>setpassword(e.target.value)} placeholder="Enter Password" type="password"/>
                            </div>
        
                            <button onClick={()=>onSubmitHandler(email,password)} class={classes.btn}>
                                Sign In
                            </button>
        
                            <div>
                                <span>New Customer?</span>
                                <a href="/"><span>Create your account</span></a>
                            </div>
                            
                        {/*</form>*/}
                            
                            
        
                        

                        <p>{message}</p>
        
                    </div>
        
                    <div class="col-sm-4">
                        
                    </div>
            </div>

           

        </div>
    )
}

export default Signin;
