import React from 'react';
import { useSelector } from 'react-redux';
import Checkoutsteps from '../../components/Checkoutsteps/Checkoutsteps';
import classes from './Placeorderscreen.module.css';

const Placeorderscreen=(props)=>{
    const shippinginfo=useSelector((state)=>state.addtocart);
    const {cartitems,shippingaddress,paymentmethod}=shippinginfo;

    const signininfo=useSelector((state)=>state.signin);
    const {user}=signininfo;
    
    if(Object.keys(user).length==0){
        props.history.push('/signin');
    }

    if(Object.keys(shippingaddress).length==0){
        props.history.push('/Shipping');
    }

    return(
        <div>
            <Checkoutsteps step1 step2 step3 step4></Checkoutsteps>
            <div class="row">
            <div class="col-sm-7">
                    <div className={classes.detailsdiv}>
                        <label>Shipping Details</label>
                        <div>
                            <label>Name: </label>
                            <p>{shippingaddress.name}</p>
                        </div>

                        <div>
                            <label>Address: </label>
                            <p>{shippingaddress.address}</p>
                        </div>
                        
                    </div>
                    <div>
                        <label>Payment Details</label>
                        <div>
                            <label>Method: </label>
                            <p>{shippingaddress.paymentmethod}</p>
                        </div>
                    </div>
                    <div>
                        <label>Ordered items</label>
                        <div>
                            <img></img>
                        </div>
                    </div>

                </div>

                <div class="col-sm-4">
                    <p>Order Summary</p>
                    <p>Items</p>
                    <p>Shipping</p>
                    <p>Order Total</p>
                    <button>Place Order</button>
                </div>

            </div>


            
        </div>
    )
}

export default Placeorderscreen;
