import React from 'react';
import   './App.module.css';
import Products from './components/Products/products';
import {BrowserRouter,Route} from 'react-router-dom';
import homescreen from './Screens/HomeScreen';
import Productscreen from './Screens/ProductScreen';

function App() {
  return (
    <BrowserRouter>  {/* Browserrouter is used where routes need to be defined*/}
      <div className="grid-container">
      <header className={classes.row}>
          <div>
              <a  href="/" className="brand">Amazon</a>
          </div>

          <div>
              <a className={classes.nav_items}  href="/cart">Cart</a>
              <a className={classes.nav_items}  href="/Signin">Signin</a>
          </div>

      </header>
      <main>
      <Route path="/product/:id" render={(props) => (
                            <Productscreen {...props}  />
                        )}/>  
        <Route path="/" component={homescreen} exact/> {/*exact means only when path is / Homescreen will be rendered*/}
      </main>

      <footer className={classes.row,classes.center}>
        Copyright @ Amazon 2021
      </footer>
  </div>
</BrowserRouter>

  )
}

export default App;
