import { PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "./Actionconstants";

const initialstate={
    product:[],
    loading:false,
    error:false

}

export const ProductScreenReducer=(state=initialstate,action)=>{
    switch(action.type){
        case PRODUCT_LIST_REQUEST:
            return{
                ...state,
                loading:false,
         
            }
        case PRODUCT_LIST_SUCCESS:
            return{
                ...state,
                loading:false,
                product:action.payload

            }
            case PRODUCT_LIST_FAIL:
                return{
                    ...state,
                    loading:false,
                    product:action.payload
    
                }
        default: 
           
            return state;
    }
}
