import React, { useEffect, useState } from 'react';
import Products from '../components/Products/products';
import Spinner from '../components/Spinner/Spinner';
import MessageBox from '../components/MessageBox/MessageBox';
import { useDispatch, useSelector } from 'react-redux';
import {ListProducts} from '../Store/Action';



const Homescreen=()=>{
  let loadSpinner={};
  let errorBox={};
  const dispatch=useDispatch();

  const productList=useSelector((state)=>state.productList);
  const {error,loading,products}=productList;


  useEffect(()=>{
    dispatch(ListProducts());
  },[dispatch]);

  {loading? loadSpinner=<Spinner/> : loadSpinner =null};
  {error? errorBox=<MessageBox>{error}</MessageBox>: errorBox=null};

    return(
        <ul className="row-products">
          
          {loadSpinner}
          {errorBox}
        {products.map(products=>(
    
          <li key={products.id} className="product-box">
                <Products link='/products/:id' product={products}/>
          
                      
          </li>

        ))}

                  
      </ul>
    )
}

export default Homescreen;
