import { CART_ITEM, CART_SAVE_SHIPPING_ADDRESS, DELETE_CART_ITEM } from "./Actionconstants";
import { useSelector } from "react-redux";



const initialstate={
    
        cartitems:[],
        shippingaddress:localStorage.getItem('shippingaddress')
        ? JSON.parse(localStorage.getItem('shippingaddress')):
        {},
 
    
}




export const CartReducer=(state=initialstate,action)=>{
    //const cartupdate=useSelector((state)=>state.deletefromcart);
    
    switch(action.type){
        case CART_ITEM:
            const item=action.payload;
            console.log(item);
           
            const itemexist=state.cartitems.find((x)=>x.product===item.product);
            console.log(itemexist);
            console.log(state.cartitems);

        
         
            if(itemexist){
                return{
                    ...state,
                    cartitems:state.cartitems.map((x)=>x.product===item.product?item:x),
                    
                }

                
            }
            else{
                
                return{
                    ...state,
                    cartitems:[...state.cartitems,item],
                    
                }
                
            }
            
        
        case DELETE_CART_ITEM:
            return{
                ...state,
                cartitems:state.cartitems.filter((item)=>item.product !== action.payload)
            }

        case CART_SAVE_SHIPPING_ADDRESS:
            return{
                ...state,
                shippingaddress:action.payload
            }

        
        
        default:
            return state;
                //cartitems:cartupdate,
            
                
    }
    

}
