import {createStore,combineReducers,applyMiddleware,compose} from 'redux';
import {ProductListReducer} from './HomeReducer';
import thunk from 'redux-thunk'; //redux - thunk
import { ProductScreenReducer } from './ProductReducer';
import { CartReducer } from './CartReducer';
import {Signinreducer} from './Signinreducer';


const iniitialState={

}

function saveToLocalStorage(state1,) {
    try {
      const serialisedState = JSON.stringify(state);
      localStorage.setItem("persistantState", serialisedState);
    } catch (e) {
      console.warn(e);
    }
  }
  
  // load string from localStarage and convert into an Object
  // invalid output must be undefined
  function loadFromLocalStorage() {
    try {
      const serialisedState = localStorage.getItem("persistantState");
      if (serialisedState === null) return undefined;
      return JSON.parse(serialisedState);
    } catch (e) {
      console.warn(e);
      return undefined;
    }
  }

  function localsaveUser(state) {
    try {
      const serialisedState = JSON.stringify(state);
      localStorage.setItem("UserState", serialisedState);
    } catch (e) {
      console.warn(e);
    }
  }

  function loadStorageUser() {
    try {
      const serialisedState = localStorage.getItem("UserState");
      if (serialisedState === null) return undefined;
      return JSON.parse(serialisedState);
    } catch (e) {
      console.warn(e);
      return undefined;
    }
  }

const reducer=combineReducers({
    productList:ProductListReducer,
    productInfo:ProductScreenReducer,
    addtocart:CartReducer,
    signin:Signinreducer,
})


const composeEnhancer= window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose; //used for using react devtools here compose comes from redux...also advanced store code used as middleware is used

const store=createStore(reducer, loadFromLocalStorage(),loadStorageUser(),composeEnhancer(applyMiddleware(thunk)));
store.subscribe(() => saveToLocalStorage(store.getState().addtocart,store.getState().signin));
//localStorage.setItem('cartItems',JSON.stringify(initialstate.cartitems));  

export default store;
