import React, { useEffect } from 'react';
import { useDispatch,useSelector } from 'react-redux';
import { AddCartItem } from '../../Store/Action';
import classes from './CartScreen.module.css';

const CartScreen = (props)=>{
    const productId=props.match.params.id;
    const Quantity=props.location.search
    ? Number(props.location.search.split('=')[1])
    : 1;
const dispatch=useDispatch();
console.log(Quantity);

const Cartitems=useSelector((state)=>state.addtocart);


    useEffect(()=>{
        if(productId){
            dispatch(AddCartItem(productId,Quantity));
        }
    },[dispatch,productId,Quantity])

    console.log(Cartitems.cartitems);


return(
        <div>
            <div className={`col-sm-8 ${classes.outerdiv}`}>
                <div >
                    <h4>Shopping cart </h4>
                </div>
               
                
                {/*Product Id : {productId}
                Product Quantity : {Quantity}*/}
                
                <div >
                {Cartitems.cartitems.map((item,i)=>{
        <li key={item.id}>
                   <div className={classes.productdetails}>
            {console.log(item,i)}
                                <div>{item.image}</div>
        
                                <div>
                                    {item.name} {productId}
                                </div>
                                {console.log(item.name)}
        
                                <div className={classes.productdmodify}>
                                    <div  className={classes.productinner}>
                                    qty {Quantity}
                                    </div>
                                    <div className={classes.productinner}> 
                                        price
                                        {console.log(item.name)}
                                    </div>
                                    <div className={classes.productinner}>
                                        delete
                                    </div>
        
                                </div>
        
                            </div>
        </li>})}
                
            

                </div>

            </div>
            <div className={`col-sm-4 ${classes.outerdiv}`}>
                <p>Subtotal</p>
                <button>Checkout</button>

            </div>
            
        </div>
        
)

}

export default CartScreen;
