import React from 'react';
import Rating from '../rating/rating';
import classes from './products.module.css';
import {Route} from 'react-router-dom';
import productscreen from '../../Screens/ProductScreen';

const products=(props)=>{
    return(
                <div>
                      <div>
                          <a href={`/product/${props.product.id}`}><img src={props.product.image} alt="" className={classes.product_image}/></a>
                      </div>
                      <div  className={classes.product_title}>
                          <a href={`/product/${props.product.id}`}><p> {props.product.name}</p></a>
                      </div>
                      <Route path="/product/:id"render={(props) => (
                            <productscreen {...props} isAuthed={true} />
                        )}/>
                      
                        <Rating rating={props.product.rating} numReviews={props.product.numReviews}/>
                          
                  
                      <div>
                          <p className={classes.product_price}> Rs {props.product.price}</p>
                      </div>
                      
                </div>
    )
}

export default products;
