import { USER_SIGNIN_FAIL, USER_SIGNIN } from "./Actionconstants"


const initialstate={
    user:[],
    message:'',
}

export const Signinreducer=(state=initialstate,action)=>{
    switch(action.type){
        case USER_SIGNIN:
            const data=action.payload.data;
            console.log(data.createdUsers);
            console.log(action.payload.email);
          
            const useremail= data.createdUsers.find((x)=>x.email === action.payload.email);

            console.log(action.payload.password);

            const userpassword= data.createdUsers.bcrypt.compareSync(action.payload.password,useremail.password);
            console.log('useremail'+ useremail)
            console.log('userpasswordd' +userpassword)
            if(useremail && userpassword){
                return{
                    ...state,
                    user:useremail,
                    message:'user found',
                }
            }
            else{
                return{
                    ...state,
                    message:'user not found'
                }
                
                
            }
       
        case USER_SIGNIN_FAIL:
            return{
                ...state,
                message:action.payload

            }
        default:
            return state;
    }

}
