import { USER_SIGNIN_FAIL, USER_SIGNIN_SUCCESS, USER_SIGNIN_REQUEST } from "./Actionconstants";

const initialstate={
    user:[],
    message:' ',
    loading:false,
}

export const Signinreducer=(state=initialstate, action)=>{
    switch(action.type){

        case USER_SIGNIN_REQUEST:
            return{
                ...state,
                loading:true,

            }
        

        case USER_SIGNIN_FAIL:
            
            return{
                ...state,
                loading:false,         
                message:action.payload,

            }
        

        
                    
        default: 
            return state;

    }

}
