import { USER_SIGNIN_FAIL, USER_SIGNIN_SUCCESS } from "./Actionconstants"

const initialstate={
    user:[],
    message:'',
}

export const Signinreducer=(state=initialstate,action)=>{
    switch(action.type){
        case USER_SIGNIN:
            const data=action.payload
            return{
                ...state,
                user:action.payload
            }
        case USER_SIGNIN_FAIL:
            return{
                ...state,
                message:action.payload

            }
        default:
            return state;
    }

}
