import { USER_SIGNIN_FAIL, USER_SIGNIN_SUCCESS } from "./Actionconstants"

const initialstate={
    user:[],
    message:'',
}

export const Signinreducer=(state=initialstate,action)=>{
    switch(action.type){
        case USER_SIGNIN:
            const data=action.payload.data;
            const userauth= data.find((x)=>x.email === action.payload.email);
            if(userauth){
                return{
                    ...state,
                    user:userauth,
                }
            }
            else{
                return{
                    ...state,
                    message:'user not found'
                }
                
                
            }
       
        case USER_SIGNIN_FAIL:
            return{
                ...state,
                message:action.payload

            }
        default:
            return state;
    }

}
