import './App.css';
import React from 'react';
import {NavLink} from 'react-router-dom';
import {BrowserRouter,Link,Route} from 'react-router-dom';
import homescreen from './Screens/HomeScreen';
import Productscreen from './Screens/ProductScreen';
import cartscreen from './Screens/Cart/CartScreen';

function App() {
  return (
    <BrowserRouter>  {/* Browserrouter is used where routes need to be defined*/}
      <div className="grid-container">
      <header className="row">
          <div>
              <NavLink to="/" className="brand">Amazon</NavLink>
          </div>

          <div>
              <NavLink className="nav_items"  to="/cart">Cart</NavLink>
              <Link className="nav_items" to="/Signin">Signin</Link>
          </div>

      </header>
      <main>
         <Route path="/product/:id" exact render={(props) => (
                            <Productscreen {...props} />
                        )}/>  
        <Route path="/cart/:id?qty=:q" component={cartscreen}></Route>
        <Route path="/" component={homescreen} exact/> {/*exact means only when path is / Homescreen will be rendered*/}
      </main>

      <footer className="row center">
        Copyright @ Amazon 2021
      </footer>
      <style>
        {"\.row{\
            display: -ms-flexbox;\
            display: flex;\
            -ms-flex-wrap: wrap;\
            flex-wrap: wrap;\
            margin-right: 0px; \
           margin-left: 0px;\
        }\
        "}
      </style>
  </div>
</BrowserRouter>

  )
}

export default App;
