import React, { useEffect, useState } from 'react';
import Products from '../components/Products/products';
import axios from "axios";
import Spinner from '../components/Spinner/Spinner';



const Homescreen=()=>{

  const[products,setproducts]=useState([]);
  const[loading,setloading]=useState([]);
  const[error,seterror]=useState(false);
  let loadSpinner={};
  useEffect(()=>{
    
    const fetchdata=async()=>{
      setloading(true); //initally loading is true
      const {data}= await axios.get('http://localhost:5000/api/products')
      setloading(false); //after data fetched loading is set to false

     
     //data from url is stored in data object which then gets stored in products using setproducts
      setproducts(data);
      console.log(products);
      console.log(setproducts);
    };

    fetchdata();
  },[]);

  {loading? loadSpinner=<Spinner/> : loadSpinner =null};


    return(
        <ul className="row-products">
          
          {loadSpinner}
        {products.map(products=>(
    
          <li key={products.id} className="product-box">
                <Products link='/products/:id' product={products}/>
          
                      
          </li>

        ))}

                  
      </ul>
    )
}

export default Homescreen;
