import { CART_ITEM } from "./Actionconstants";

const initialstate={
    cart:{
        cartitems:localStorage.getItem('cartItems')
        ?
        JSON.parse(localStorage.getItem('cartItems'))
        :[]
    }
}

export const CartReducer=(state=initialstate,action)=>{
   
    switch(action.type){
        case CART_ITEM:{
            const item=action.payload;
            const itemexist=state.cart.cartitems.find((x)=>x.product===item.product);
            console.log(itemexist);
            console.log(state.cartitems);
            localStorage.setItem('cartItems',JSON.stringify(initialstate.cart.cartitems));
            if(itemexist){
                return{
                    ...state,
                    cartitems:state.cartitems.map((x)=>x.product===item.product?item:x),
                }
                
            }
            else{
                return{
                    ...state,
                    cartitems:[...state.cartitems,item],
                }
                
            }
            
        }
        default:
            return{
                ...state,
                
            }
    }
    

}
