import React from 'react';

const rating=(props)=>{
    return(
        <span className="rating">
            <i className={props.rating >=1 ? "fas fa-star" 
            : props.rating >=0.5 
            ? "fas fa-star-half-alt"  
            : "far fa-star" }></i>
            <i className={props.rating >=1 ? "fas fa-star" 
            : props.rating >=0.5 
            ? "fas fa-star-half-alt"  
            : "far fa-star" }></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star-half-alt"></i>
        </span>
    )
}

export default rating;
