import { USER_SIGNIN_FAIL, USER_SIGNIN, USER_SIGNIN_SUCCESS, USER_SIGNIN_REQUEST } from "./Actionconstants"
import bcrypt from 'bcryptjs';

const initialstate={
    user:{},
    message:'',
    loading:false,
}

export const Signinreducer=(state=initialstate,action)=>{
    switch(action.type){

        case USER_SIGNIN_REQUEST:{
            return{
                ...state,
                loading:true

            }
        }

        case USER_SIGNIN_SUCCESS:{
            const data=action.payload;
            console.log(data);
            
            if(data){
                return{
                    ...state,
                        user:data,
                        message:'user found',
                }
            }

        }
        case USER_SIGNIN_FAIL:{
            return{
              ...state,
                message:action.payload,

            }
        }

        
        default:
            return state;
    }

}
