import React, { useEffect, useState } from 'react';
import Products from '../components/Products/products';
import axios from "axios";



const homescreen=()=>{

  const[products,setproducts]=useState([]);

  useEffect(()=>{
    const data={};
    const fetchdata=async()=>{
      data= axios.get('/api/products');  //data from url is stored in data project which then gets stored in products using setproducts
      setproducts(data);
    }

    fetchdata();

  },[])


    return(
        <ul className="row-products">
        {products.map(products=>(
    
          <li key={products.id} className="product-box">
                <Products link='/products/:id' product={products}/>
          
                      
          </li>

        ))}

                  
      </ul>
    )
}

export default homescreen;
