import { PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "./Actionconstants";
initialState={
    loading:false,
    error:false,
    products:[]
}
export const ProductListReducer=(stateinitialState,action)=>{
    switch(action.type){
        case PRODUCT_LIST_REQUEST:
            return{
                loading:true
            }

        case PRODUCT_LIST_SUCCESS:
            return{
                loading:false,
                data:action.payload
                
            }

        case PRODUCT_LIST_FAIL:
            return{
                loading:false,
                error:action.payload
            }
    }
}
