import { CART_ITEM, DELETE_CART_ITEM } from "./Actionconstants";

const initialstate={
    
        cartitems:[],
 
    
}




export const CartReducer=(state=initialstate,action)=>{
   
    switch(action.type){
        case CART_ITEM:
            const item=action.payload;
            console.log(item);
           
            const itemexist=state.cartitems.find((x)=>x.product===item.product);
            console.log(itemexist);
            console.log(state.cartitems);

        
         
            if(itemexist){
                return{
                    ...state,
                    cartitems:state.cartitems.map((x)=>x.product===item.product?item:x),
                    
                }

                
            }
            else{
                
                return{
                    ...state,
                    cartitems:[...state.cartitems,item],
                    
                }
                
            }
            
        
        case DELETE_CART_ITEM:
            return{
                ...state,
                cartitems:cartitems.filter((item)=>item.product !== action.payload)
            }

        
        
        default:
            return state;
                
    }
    

}
