import { DELETE_CART_ITEM } from "./Actionconstants"

const Cartitems=useSelecto((state)=>state.addtocart);

const initialstate={
    dcartitems:Cartitems.cartitems

}


export const Deleteitemreducer=(state=initialstate,action)=>{

    switch(action.type){
        case DELETE_CART_ITEM:
            return{
                ...state,
                dcartitems:dcartitems.filter(((item)=>item.product !== action.payload))
            }
        default:
            return state

    }
}
