import { useSelector } from "react-redux";
import { DELETE_CART_ITEM } from "./Actionconstants"



const initialstate={
    dcartitems:[]

}
/*

export const Deleteitemreducer=(state=initialstate,action)=>{
    
    
    switch(action.type){
        case  DELETE_CART_ITEM:
            return{
                ...state,
                dcartitems:state.dcartitems.filter(((item)=>item.product !== action.payload))
            }
        default:
            return state

    }
}
*/
